mapper.select:
select distinct mm.registno as registno, mm.querytime as querytime, mm.vincode as vincode, md.reqpartname as reqpartname, md.reqpartno as reqpartno, md.reqpartprice as reqpartprice, md.standardpartno as standardpartno, md.standardprice as standardprice, pdf.handlercode as handlercode, pdf.handlername as handlername, pdf.repairfactoryname as repairfactoryname from prplbpmmain bpm,prpldeflossmain pdf ,prplcomponent pcp,prpldeflossthirdparty pdt,mingjue_main mm,mingjue_data md where bpm.businessid = pdf.id and pdf.id = pcp.prpldeflossmainid and pdf.prplthirdpartid = pdt.id and mm.repartinfoid = md.mingjuemainid and bpm.businessno=mm.registno
and md.standardpartno = #{standardpartno}
and md.reqpartno = #{reqpartno}
and bpm.businessno = #{businessno}
